def iniciarConsultasGui(window):
    window.destroy()
    if mc.comprobar_Permisos() == 3:
        consultaC.iniciar()
    elif mc.comprobar_Permisos() == 2:
        consultaE.iniciar()
    else:
        consultasA.iniciar()


