def cerrar(app):
    try:
        lc.cerrarSesion()
        app.destroy()
    except:
        app.destroy()  

def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

def agregarMunicipio(codigo, nombre, poblacion, tipoMunicipio, departamento):
    mc.agregarMunicipio(codigo, nombre, poblacion, tipoMunicipio, departamento)

def buscarMunicipio(codigo, tfNombre, tfPoblacion, tfTipoMunicipio, tfdepartamento):
    municipio = mc.buscarMunicipio(codigo)

    tfNombre.delete(0, END)  
    tfPoblacion.delete(0, END)
    tfTipoMunicipio.delete(0, END)
    tfdepartamento.delete(0, END)

    tfNombre.insert(0, municipio[0][1])
    tfPoblacion.insert(0, municipio[0][2])
    tfTipoMunicipio.insert(0, municipio[0][3])
    tfdepartamento.insert(0, municipio[0][4])

def actualizarMunicipio(codigo, nombre, poblacion, tipoMunicipio, departamento):
    mc.actualizarMunicipio(codigo, nombre, poblacion, tipoMunicipio, departamento)


def eliminarMunicipio(codigo):
    mc.eliminarMunicipio(codigo)

