def registrarProfesion(codigo, nombre):
    pc.registrarProfesion(codigo,nombre)

def buscarProfesion(codigo, nombre):
    profesion = pc.obtenerProfesion(codigo)
    nombre.delete(0, END)

    nombre.insert(0, profesion[0][1])

def actualizarProfesion(codigo, nombre):
    pc.actualizarProfesion(codigo, nombre)

def eliminarProfesion(codigo):
    pc.eliminarProfesion(codigo)

